import React from 'react'
import PropTypes from 'prop-types'
import _ from "lodash/fp";
import { useHistory } from 'react-router-dom';
import { useForm } from 'react-hook-form';

const PostAdd = ({ onAddPost }) => {
    let history = useHistory();
    const [valueInput, setValueInput] = useState({});

    const onSubmit = (data) => {
        onAddPost(data);
        history.push("/admin/products");
    }
    const { register, handleSubmit, watch, errors } = useForm(); 
    return (
        <div>
            <section class="content">
                <h3 className="card-title">Thêm bài viết</h3>
                <form onSubmit={handleSubmit(onSubmit)}>
                    <div className="form-group">
                        <label htmlFor="exampleInputEmail1">Tiêu đề</label>
                        <input type="text"
                            name="title"
                            className="form-control"
                            id="inputProductName"
                            aria-describedby="emailHelp" 
                            ref={register({ required: true, maxLength: 30 })}/>
                            {_.get("title.type", errors) === "required" && (<span style = {{ color: "red" }}>Tên sản phẩm không được để trống</span>)}
                            {_.get("title.type", errors) === "maxLength" && (<span style = {{ color: "red" }}>Tên sản phẩm không quá 30 ký tự</span>)}
                    </div>
                    <div className="form-group">
                        <label htmlFor="exampleInputEmail1">Ảnh sản phẩm</label>
                        <input type="text"
                            name="image"
                            className="form-control"
                            id="inputProductName"
                            aria-describedby="emailHelp" 
                            ref={register({ required: true })}/>
                            {errors.price && <span style = {{ color: "red" }}>Giá sản phẩm không được bỏ trống</span>}
                    </div>
                    <div className="form-group">
                        <label htmlFor="exampleInputEmail1">Chi tiết bài viết</label>
                        <input type="text-area"
                            name="image"
                            className="form-control"
                            id="inputProductName"
                            aria-describedby="emailHelp" 
                            ref={register({ required: true })}/>
                             {errors.image && <span style = {{ color: "red" }}>Ảnh sản phẩm không được bỏ trống</span>}
                    </div>
                    
                    <div className="form-group">
                        <label htmlFor="exampleInputEmail1">Chọn danh mục</label>
                        <select className="form-control" name="category_id" ref={register({ required: true })}>
                        {category.map(({id, name},index) =>(
                            <option value = {id}>{name}</option>
                        ))}
                        </select>
                    </div>
                    <button onSubmit={handleSubmit(onSubmit)} type="submit" className="btn btn-primary">Submit</button>
                </form>
            </section>
        </div>
    )
}

PostAdd.propTypes = {

}

export default PostAdd
