import React, { useState } from 'react'
import PropTypes from 'prop-types';
import { useForm } from "react-hook-form";
import { useParams, useHistory } from  'react-router-dom';



const ProductsEdit = ({ products, onEdit }) => {
    let { id } = useParams();
    let history = useHistory();
    const product = products.find(product => product.id === id);

    const [currentProduct, setCurrentProduct] = useState(product);

    const onSubmit = (e) => {
        e.preventDefault();
        onEdit(currentProduct);
        history.push('/admin/products');
    }
    const onHandleChange = e => {
        const { name, value } = e.target;
        setCurrentProduct({
            ...currentProduct,
            [name]: value
        })
    }

    const { register, handleSubmit, watch, errors } = useForm();
    return (
        <div>
            <form onSubmit={handleSubmit(onSubmit)} className="w-50">
                <div className="form-group">
                    <label htmlFor="productName">Tên sản phẩm</label>
                    <input type="text" name="name" 
                    value={currentProduct.name} 
                    onChange={onHandleChange} 
                    className="form-control" 
                   />
                  {("name.type", errors) === "required" && <span style = {{ color: "red" }}>Tên sản phẩm không được bỏ trống</span>}
                  {("name.type", errors) === "maxLength" && <span style = {{ color: "red" }}>Tên sản phẩm không quá 25 ký tự</span>}
                  
                </div>
                <div className="form-group">
                    <label htmlFor="productName">Giá sản phẩm</label>
                    <input type="text" 
                    name="price" value={currentProduct.price} 
                    onChange={onHandleChange} 
                    className="form-control" 
                    ref={register({ required: true })}
                    />
                    {errors.price && <span>Vui lòng không được bỏ trống</span>}
                </div>
                <button className="btn btn-primary">Cập nhật</button>
            </form>
        </div>
    )
}

ProductsEdit.propTypes = {

}

export default ProductsEdit
