import React,{useState} from 'react'
import PropTypes from 'prop-types'
import _ from "lodash/fp";
import { useForm } from "react-hook-form";
import { useParams, useHistory } from  'react-router-dom';

function PostEdit ({ post, onEditPost }) {
  let { id } = useParams();
  let history = useHistory();
  let posts = post.find((data) => data.id == id);

  const [currentPost, setCurrentPost] = useState(posts);

  function onHandleChange (e) {
    const { title ,image,content, value } = e.target;
    setCurrentPost({
      ...currentPost,
      [name]: value,
      [image]: value,
      [content]: value
    });
  };

  function onSubmit (e) {
    onEditPost(currentPost);
    history.push("/admin/post");
  };
  const { register, handleSubmit, watch, errors } = useForm();
    return (
        <div>
            <form onSubmit={handleSubmit(onSubmit)} className="w-50">
                <div className="form-group">
                    <label htmlFor="productName">Tiêu đề</label>
                    <input type="text" name="title" 
                    value={currentPost.title} 
                    onChange={onHandleChange} 
                    className="form-control"
                    ref={register({
                        required: true,
                        maxLength: 30,
                      })} 
                   />
                    {_.get("title.type", errors) === "required" && (<span style = {{ color: "red" }}>Tên tiêu đề không được để trống</span>)}
                </div>
                <div className="form-group">
                    <label htmlFor="productName">Ảnh bài viết</label>
                    <input type="text" name="image" 
                    value={currentPost.image} 
                    onChange={onHandleChange} 
                    className="form-control"
                    ref={register({
                        required: true,
                        maxLength: 30,
                      })} 
                   />
                    {_.get("image.type", errors) === "required" && (<span style = {{ color: "red" }}>Tên sản phẩm không được để trống</span>)}
                </div>
                <div className="form-group">
                    <label htmlFor="productName">Nội dung bài viết</label>
                    <input type="text" name="content" 
                    value={currentPost.content} 
                    onChange={onHandleChange} 
                    className="form-control"
                    ref={register({
                        required: true,
                        maxLength: 30,
                      })} 
                   />
                    {_.get("content.type", errors) === "required" && (<span style = {{ color: "red" }}>Nội dung bài viết không được để trống</span>)}
                </div>
                <button className="btn btn-primary">Cập nhật</button>
            </form>
        </div>
    )
}

PostEdit.propTypes = {

}

export default PostEdit
