import React, { useState } from 'react'
import PropTypes from 'prop-types';
import _ from "lodash/fp";
import { useForm } from "react-hook-form";
import { useParams, useHistory } from  'react-router-dom';



const ProductsEdit = ({ products, onEdit }) => {
    let { id } = useParams();
    let history = useHistory();
    const product = products.find(product => product.id === id);

    const [currentProduct, setCurrentProduct] = useState(product);

    function onSubmit () {
        // e.preventDefault();
        onEdit(currentProduct);
        history.push('/admin/products');
    }
    function onHandleChange  (e){
        const { name, value } = e.target;
        setCurrentProduct({
            ...currentProduct,
            [name]: value
        })
    }

    const { register, handleSubmit, watch, errors } = useForm();
    return (
        <div>
            <form onSubmit={handleSubmit(onSubmit)} className="w-50">
                <div className="form-group">
                    <label htmlFor="productName">Tên sản phẩm</label>
                    <input type="text" name="name" 
                    value={currentProduct.name} 
                    onChange={onHandleChange} 
                    className="form-control"
                    ref={register({
                        required: true,
                        maxLength: 20,
                        pattern: /^[A-Za-z]+$/i
                      })} 
                   />
                   {_.get("firstName.type", errors) === "required" && (<p>This field is required</p>)}
      {_.get("firstName.type", errors) === "maxLength" && (
        <p>First name cannot exceed 20 characters</p>
      )}
      {_.get("firstName.type", errors) === "pattern" && (
        <p>Alphabetical characters only</p>
      )}
                </div>
                <div className="form-group">
                    <label htmlFor="productName">Giá sản phẩm</label>
                    <input type="text" 
                    name="price" value={currentProduct.price} 
                    onChange={onHandleChange} 
                    className="form-control"
                    />
                 
                </div>
                <button className="btn btn-primary">Cập nhật</button>
            </form>
        </div>
    )
}

ProductsEdit.propTypes = {

}

export default ProductsEdit
