import React, { useState } from 'react'
import PropTypes from 'prop-types';
import _ from "lodash/fp";
import { useParams, useHistory } from  'react-router-dom';
import { useForm } from "react-hook-form";


const ProductsEdit = ({ products, onEdit }) => {
    let { id } = useParams();
    let history = useHistory();
    const product = products.find(product => product.id === id);

    const [currentProduct, setCurrentProduct] = useState(product);

    const onHandleSubmit = (e) => {
        e.preventDefault();
        onEdit(currentProduct);
        history.push('/admin/products');
    }
    const onHandleChange = e => {
        const { name, value } = e.target;
        setCurrentProduct({
            ...currentProduct,
            [name]: value
        })
    }

    const { register, handleSubmit, watch, errors } = useForm();
    return (
        <div>
            <form action="" onSubmit={handleSubmit()} className="w-50">
                <div className="form-group">
                    <label htmlFor="productName">Tên sản phẩm</label>
                    <input type="text" name="name" 
                    value={currentProduct.name} 
                    onChange={onHandleChange} 
                    className="form-control" 
                    ref={register({ required: true,maxLength: 25,pattern: /^[A-Za-z]+$/i })}/>
                  {_.get("name.type", errors) === "required" && <span style={(color: red)}>Vui lòng không được bỏ trống</span>}
                  {_.get("name.type", errors) === "maxLength" && <span>Tên sản phẩm không quá 25 ký tự</span>}
                  {_.get("name.type", errors) === "pattern" && <span>Tên sản phẩm không phải là ký tự đặc biệt</span>}
                </div>
                <div className="form-group">
                    <label htmlFor="productName">Giá sản phẩm</label>
                    <input type="text" 
                    name="price" value={currentProduct.price} 
                    onChange={onHandleChange} 
                    className="form-control" 
                    ref={register({ required: true })}
                    />

                </div>
                <button className="btn btn-primary">Cập nhật</button>
            </form>
        </div>
    )
}

ProductsEdit.propTypes = {

}

export default ProductsEdit
