import React from 'react'
import { BrowserRouter as Router, Route, Switch } from 'react-router-dom';
import LayoutMain from '../pages/layouts/LayoutMain'
import LayoutAdmin from '../pages/layouts/LayoutAdmin'
//Admin
import Dashboard from '../pages/views/Admin/Dashboard'
import ProductsManager from '../pages/views/Admin/Products';
import CategoryManager from '../pages/views/Admin/Category';
import ProductsAdd from '../pages/views/Admin/Products/ProductsAdd';
import ProductsDetail from '../pages/views/Admin/Products/ProductsDetail';
import ProductsEdit from '../pages/views/Admin/Products/ProductsEdit';
//Views
import About from '../pages/views/Main/About'
import Home from '../pages/views/Main/Home'




const Routers = ({ products, category, onRemove, onAdd, onEdit, onRemoveCate }) => {
    const onHandleRemove = (id) => {
        onRemove(id)
    }
    const onHandleAdd = (product) => {
        onAdd(product)
    }
    const onHandleEdit = (id, product) =>{
        onEdit(id, product)
    }
    const onHandleRemoveCate = (id) => {
        onRemove(id)
    }
    return (
        <Router>
            <Switch>
                <Route path="/admin/:path?/:path?" exact>
                    <LayoutAdmin>
                        <Switch>
                            <Route path='/admin' exact>
                                <Dashboard />
                            </Route>
                            <Route path='/admin/products'>
                                <ProductsManager products={products} onRemove={onHandleRemove} />
                            </Route>
                            <Route path='/admin/add-product' >
                                <ProductsAdd onAdd={onHandleAdd}/>
                            </Route>
                            <Route path='/admin/detail-product/:id'>
                                <ProductsDetail products={products} />
                            </Route>
                            <Route path='/admin/edit-product/:id'>
                                <ProductsEdit products={products} onEdit={onHandleEdit} />
                            </Route>
                            <Route path='/admin/category'>
                                <CategoryManager category={category} onRemove={onRemoveCate}/>
                            </Route>
                        </Switch>
                    </LayoutAdmin>
                </Route>
                <Route>
                    <LayoutMain>
                        <Switch>
                            <Route path="/" exact>
                                <Home products = { products }/>
                            </Route>
                            <Route path="/product-detail/:id">
                                <About products = { products }/>
                            </Route>
                        </Switch>
                    </LayoutMain>
                </Route>
            </Switch>
        </Router>
    )
}

Routers.propTypes = {

}

export default Routers
