import React, {useState} from 'react'
import PropTypes from 'prop-types'
import _ from "lodash/fp";
import { useForm } from "react-hook-form";
import { useParams, useHistory } from  'react-router-dom';

const PostEdit = ({ post, onEditPost }) => {
    
    let {id} = useParams();
    let history = useHistory();
    let posted = post.find((data) => data.id == id);

    const [currentPost, setCurrentPost] = useState(posted);

    const onSubmitPost = (e) => {
        onEditPost(currentPost);
        history.push("/admin/post");
        };
        
    const onHandleChange = (e) => {
        const { title, value } = e.target;
        setCurrentPost({
          ...currentPost,
          [title]: value,
        });
      };

    

    const { register, handleSubmit, watch, errors } = useForm();  
    return (
        <div>
            <form onSubmit={handleSubmit(onSubmitPost)} className="w-50">
                <div className="form-group">
                    <label htmlFor="postTitle">Tiêu đề</label>
                    <input type="text" name="title" 
                    defaultValue={currentPost.title} 
                    onChange={onHandleChange} 
                    className="form-control"
                    ref={register({
                        required: true,
                        maxLength: 30,
                      })} 
                   />
                    {_.get("title.type", errors) === "required" && (<span style = {{ color: "red" }}>Tên sản phẩm không được để trống</span>)}
                    {_.get("title.type", errors) === "maxLength" && (<span style = {{ color: "red" }}>Tên sản phẩm không quá 30 ký tự</span>)}
                </div>
                <button className="btn btn-primary">Cập nhật</button>
            </form>
        </div>
    )
}

PostEdit.propTypes = {

}

export default PostEdit
