import React, { useState } from 'react';
// import Products from './components/Products';
// import Box from './components/Box/Box';
// import Home from './components/Main/NavBar'
// import Product from './components/Product';
import dataFake from './dataFake';
import Routers from './routers'

// import AddProduct from './components/AddProduct';
function App() {

  const [products, setProducts] = useState(dataFake);
  const [status, setStatus] = useState(false);
  const onHandleClick = () => {
    // setStatus(true);
    setColor('red');
  }

  //Xóa sản phẩm
  const onHandleRemove = (id) => {
    const newProducts = products.filter(product => product.id !== id);
    setProducts(newProducts);
  }

  //Thêm sản phẩm 
  const onHandleAdd = (product) => {
    const newProduct = {
      id: products.length + 1,
      ...product
    }
    setProducts([
      ...products,
      newProduct
    ])
  }

  //Chỉnh sửa sản phẩm
  const onHandleEdit = ( updateProduct ) =>{
    const newProducts = products.map(product => (
      product.id === updateProduct.id? updateProduct : product // Nếu product.id = id sản phẩm thì chỉnh sửa, nếu không bằng thì trả về một mảng có object mới
    ));
    localStorage.setItem('products', JSON.stringify(newProducts))
    setProducts(newProducts);
  }

  return (
    <div className="App">
      <Routers products={products} onRemove={onHandleRemove} onAdd={onHandleAdd} onEdit={onHandleEdit}/>
    </div>
  )

}
export default App;
