import React, { useState,useEffect } from 'react';
import Routers from './routers'
import apiRequest from './api/productApi'

// import AddProduct from './components/AddProduct';
function App() {

  const [products, setProducts] = useState([apiRequest]);

  useEffect(() => {
    const getProducts = async () => {
      try {
        const { data } = await apiRequest.getAll();
        setProducts(data);
      } catch (error) {
        console.log('failed to request API: ', error)
      }
    }
    getProducts();
  }, []);

  //Xóa sản phẩm
  const onHandleRemove = (id) => {
    const newProducts = products.filter(product => product.id !== id);
    setProducts(newProducts);
  }

  //Thêm sản phẩm 
  const onHandleAdd = async (product) => {
    try {
      const { data } = await apiRequest.create(product);
      setProducts([
        ...products,
        data
      ])
    } catch (error) {
      console.log('failed to request API: ', error)
    }
  }

  //Chỉnh sửa sản phẩm
  const onHandleEdit = async ( updateProduct ) =>{
    const newProducts = products.map(product => (
      product.id === updateProduct.id? updateProduct : product // Nếu product.id = id sản phẩm thì chỉnh sửa, nếu không bằng thì trả về một mảng có object mới
    ));
    localStorage.setItem('products', JSON.stringify(newProducts))
    setProducts(newProducts);
  }

  return (
    <div className="App">
      <Routers products={products} onRemove={onHandleRemove} onAdd={onHandleAdd} onEdit={onHandleEdit}/>
    </div>
  )

}
export default App;
