import React, { useState } from 'react'
import PropTypes from 'prop-types'
import { useHistory } from 'react-router-dom';
import { useForm } from 'react-hook-form';


function ProductsAdd({ onAdd }) {
    let history = useHistory();
    const [valueInput, setValueInput] = useState({});
   
    function onHandleChange(e) {
        const { name, image, price, Desc, value } = e.target;
        setValueInput({
            ...valueInput,
            [name]: e.target.value,
            [image]: e.target.value,
            [price]: e.target.value,
            [Desc]: e.target.value,
        })
    }

    function onSubmit() {
        onAdd(valueInput);
        history.push("/admin/products");
    }

    const { register, handleSubmit, watch, errors } = useForm();
    return (
        <div>
            <section class="content">
                <h3 className="card-title">Thêm sản phẩm</h3>
                <form onSubmit={handleSubmit(onSubmit)}>
                    <div className="form-group">
                        <label htmlFor="exampleInputEmail1">Tên sản phẩm</label>
                        <input type="text"
                            name="name"
                            className="form-control"
                            id="inputProductName"
                            onChange={onHandleChange} aria-describedby="emailHelp" 
                            ref={register({ required: true })}/>
                            {errors.name && <span style = {( color:red )}>Vui lòng không được bỏ trống</span>}
                    </div>
                    <div className="form-group">
                        <label htmlFor="exampleInputEmail1">Gía sản phẩm</label>
                        <input type="number"
                            name="price"
                            className="form-control"
                            id="inputProductName"
                            onChange={onHandleChange} aria-describedby="emailHelp" 
                            ref={register({ required: true })}/>
                            {errors.price && <span>Vui lòng không được bỏ trống</span>}
                    </div>
                    <div className="form-group">
                        <label htmlFor="exampleInputEmail1">Ảnh sản phẩm</label>
                        <input type="text"
                            name="image"
                            className="form-control"
                            id="inputProductName"
                            onChange={onHandleChange} aria-describedby="emailHelp" 
                            ref={register({ required: true })}/>
                             {errors.image && <span>Vui lòng không được bỏ trống</span>}
                    </div>
                    {/* <div className="form-group">
                        <label htmlFor="exampleInputEmail1">Mô tả sản phẩm</label>
                        <textarea className="form-control"
                            name="Desc" id="inputProductName"
                            onChange={onHandleChange} />
                    </div> */}
                    <button onSubmit={handleSubmit(onSubmit)} type="submit" className="btn btn-primary">Submit</button>
                </form>
            </section>
        </div>
    )
}

export default ProductsAdd;
