import React,{useState} from 'react'
import PropTypes from 'prop-types'
import { useHistory } from 'react-router-dom';
function ProductsAdd({ onAdd }) {
    let history = useHistory();
    const [valueInput, setValueInput] = useState({});
    function onHandleChange(e) {
        const { name, image, price, Desc, value } = e.target;
        setValueInput({
            ...valueInput,
            [name]: e.target.value,
            [image]: e.target.value,
            [price]: e.target.value,
            [Desc]: e.target.value,
        })
    }
    function onSubmit() {
        onAdd(valueInput);
        history.push("/admin/products")

    return (
        <div>
            <div className="content-wrapper">
                <div className="card">
                    <div className="card-header">
                        <h3 className="card-title">Thêm sản phẩm</h3>
                        <br />
                        <form onSubmit="">
                            <div className="form-group">
                                    <label htmlFor="inputProductName">Tên sản phẩm</label>
                                    <input name="name" type="text" className="form-control" id="inputProductName" />
                            </div>
                            <div className="form-group">
                                    <label htmlFor="inputProductImage">Ảnh sản phẩm</label>
                                    <input name="image" type="text" className="form-control" id="inputProductImage"/>
                            </div>
                            <div className="form-group">
                                    <label htmlFor="inputProductPrice">Gía sản phẩm</label>
                                    <input name="price" type="text" className="form-control" id="inputProductPrice"/>
                            </div>
                            <button type="submit" className="btn btn-primary" onClick={onSubmit}>Submit</button>
                        </form>
                    </div>
                </div>
                </div>
        </div>
    )
}
}

ProductsAdd.propTypes = {

}

export default ProductsAdd
