import React, {useState} from 'react'
import PropTypes from 'prop-types'
import _ from "lodash/fp";
import { useForm } from "react-hook-form";
import { useParams, useHistory } from  'react-router-dom';

function PostEdit ({ post, onEditPost }) {
    let {id} = useParams();
    let history = useHistory();
    let posts = post.find((data) => data.id == id);

    const [currentPost, setCurrentPost] = useState(posts);

    function onHandleChange (e) {
        const { title, value } = e.target;
        setCurrentPost({
          ...currentPost,
          [title]: value,
        });
      };

    function onSubmit (e) {
    onEditPost(currentPost);
    history.push("/admin/post");
    };

    const { register, handleSubmit, watch, errors } = useForm();  
    return (
        <div>
            <form onSubmit={handleSubmit(onSubmit)} className="w-50">
                <div className="form-group">
                    <label htmlFor="productName">Tiêu đề</label>
                    <input type="text" name="name" 
                    value={currentPost.name} 
                    onChange={onHandleChange} 
                    className="form-control"
                    ref={register({
                        required: true,
                        maxLength: 30,
                      })} 
                   />
                    {_.get("name.type", errors) === "required" && (<span style = {{ color: "red" }}>Tên sản phẩm không được để trống</span>)}
                    {_.get("name.type", errors) === "maxLength" && (<span style = {{ color: "red" }}>Tên sản phẩm không quá 30 ký tự</span>)}
                </div>
                <button className="btn btn-primary">Cập nhật</button>
            </form>
        </div>
    )
}

PostEdit.propTypes = {

}

export default PostEdit
