import React, { useState } from 'react'
import PropTypes from 'prop-types'
import _ from "lodash/fp";
import { useForm } from "react-hook-form";
import { useParams, useHistory } from  'react-router-dom';


function CategoryEdit ({ category,onEditCate }) {
  let { id } = useParams();
  let history = useHistory();
  let categories = category.find((data) => data.id == id);
  const [currentCategory, setCurrentCategory] = useState(categories);
 
  const onSubmit = (e) => {
    onEditCate(currentCategory);
    history.push("/admin/category");
  };
  const onHandleChange = (e) => {
    const { name, value } = e.target;
    setCurrentCategory({
      ...currentCategory,
      [name]: value,
    });
  };

    const { register, handleSubmit, watch, errors } = useForm();
    return (
        <div>
            <form onSubmit={handleSubmit(onSubmit)} className="w-50">
                <div className="form-group">
                    <label htmlFor="productName">Tên danh mục</label>
                    <input type="text" name="name" 
                    value={currentCategory.name} 
                    onChange={onHandleChange} 
                    className="form-control"
                    ref={register({
                        required: true,
                        maxLength: 30,
                      })} 
                   />
                    {_.get("name.type", errors) === "required" && (<span style = {{ color: "red" }}>Tên sản phẩm không được để trống</span>)}
                    {_.get("name.type", errors) === "maxLength" && (<span style = {{ color: "red" }}>Tên sản phẩm không quá 30 ký tự</span>)}
                </div>
                <button className="btn btn-primary">Cập nhật</button>
            </form>
        </div>
    )
}

CategoryEdit.propTypes = {

}

export default CategoryEdit
